import React from "react";
import {Link} from "react-router-dom";
import PracticeOn from "./PracticeOn";

export default class Practice extends React.Component {
    constructor(props){
        super(props);
        this.getValuesFromInput = this.getValuesFromInput.bind(this);
    }
    getValuesFromInput(){
        sentence = document.getElementById('sentence').value;
        size = document.getElementById('size'),value;
        this.props.setValues(sentence, size);
    }
    render() {
        return (
            <div>
                <div id = "sentence">
                    Sentence to Chunk : <input type = "text" ></input>
                </div>
                <div id = "size">
                    Size of Chunk : <input type = "text" ></input>
                </div>
                <Link to = "/practice/on-going">
                    <button onClick ={this.getValuesFromInput}>OK</button>
                </Link>
                <Link to = "/">
                    <button>Cancel</button>
                </Link>
            </div>
        );
    }
}
